import React, { useState, useEffect } from 'react';
import api from '../api';

const TaskList = () => {
  const [tareas, setTareas] = useState([]);

  useEffect(() => {
    const fetchTareas = async () => {
      try {
        const response = await api.get('/tareas');
        setTareas(response.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchTareas();
  }, []);

  return (
    <div>
      <h2>Lista de Tareas</h2>
      <ul>
        {tareas.map(tarea => (
          <li key={tarea._id}>
            {tarea.descripcion} - {tarea.estado} - {tarea.diasRetraso ? `Retraso: ${tarea.diasRetraso} días` : 'A tiempo'}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
