import React, { useState } from 'react';
import { Container, Typography, Button, TextField, MenuItem } from '@mui/material';
import TaskList from './components/TaskList';
import TaskFilter from './components/TaskFilter';

const App = () => {
  const [tareas, setTareas] = useState([]);

  return (
    <Container maxWidth="md" style={{ backgroundColor: '#fff', padding: '20px', marginTop: '50px', borderRadius: '8px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
      <Typography variant="h4" align="center" gutterBottom>Task Manager</Typography>
      <TaskFilter setTareas={setTareas} />
      <TaskList tareas={tareas} />
    </Container>
  );
};

export default App;
