import React from 'react';

const TaskList = ({ tareas }) => {
  return (
    <div>
      <h2>Lista de Tareas</h2>
      <ul>
        {tareas.map(tarea => (
          <li key={tarea._id}>
            {tarea.descripcion} - {tarea.estado} - {tarea.diasRetraso ? `Retraso: ${tarea.diasRetraso} días` : 'A tiempo'}<br/>
            Empleado: {tarea.idEmpleado.nombre} {tarea.idEmpleado.apellido} - Proyecto: {tarea.idProyecto.nombre}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
