import React from 'react';

const TaskList = ({ tareas }) => {
  return (
    <div>
      <h2>Lista de Tareas</h2>
      <ul>
        {tareas.map(tarea => (
          <li key={tarea._id}>
            <strong>Descripción:</strong> {tarea.descripcion} - <strong>Estado:</strong> {tarea.estado} - <strong>Retraso:</strong> {tarea.diasRetraso ? `${tarea.diasRetraso} días` : 'A tiempo'}
            <br/>
            <strong>Empleado:</strong> {tarea.idEmpleado ? `${tarea.idEmpleado.nombre} ${tarea.idEmpleado.apellido}` : 'Empleado no asignado'} - <strong>Proyecto:</strong> {tarea.idProyecto ? tarea.idProyecto.nombre : 'Proyecto no asignado'}
            <br/>
            <strong>Fecha de Inicio:</strong> {new Date(tarea.fechaInicio).toLocaleDateString()} - <strong>Días Estimados:</strong> {tarea.diasEstimados}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
